	AREA	project, CODE, READWRITE
		ENTRY

Main
	LDR	sp, ST						;set stack pointer address
	LDR	r0,=Random_data		;address of 1000's data
	LDR	r1,=Search_data		;address of 100's data of search
	LDR	r2,=Array_data			;address of 1000's sorted data
	MOV lr, pc						;lr=pc
	B QuickSort						;call QuickSort function
	
	;store the sort array
StoreArray
	STMEA sp!, {r0-r12}		;store registers value in stack
	MOV r1, #1						;r1=1 for count
	
StoreLoop									;store 1000's sorted data in Array_data 
	LDMFD r0!, {r3-r12}					;load 10's data to sorted array
	MOV r12, r12, LSL #16				;combine of two 4byte data to 4byte size(2+2)
	ADD r12, r11, r12
	MOV r10, r10, LSL #16				;combine of two 4byte data to 4byte size(2+2)
	ADD r10, r9, r10
	MOV r8, r8, LSL #16					;combine of two 4byte data to 4byte size(2+2)
	ADD r8, r7, r8
	MOV r6, r6, LSL #16					;combine of two 4byte data to 4byte size(2+2)
	ADD r6, r5, r6
	MOV r4, r4, LSL #16					;combine of two 4byte data to 4byte size(2+2)
	ADD r4, r3, r4
	STMEA r2!, {r4,r6,r8,r10,r12}	;store combined data in Array_data(r2)
	
	CMP r1, #100							;if r1==100, exit loop
	ADD	r1, r1, #1							;else r1++
	BNE StoreLoop
	
	;search start
	LDMEA sp!, {r0-r12}				;load previous data to stack 
	SUB r3, r1, #4							;r3 is last addr
	MOV r9, #1								;r9 is count of search loop
	MOV r10, r0								;r10 is copy of first addr
	LDR r11, Result						;address that store success searched data 
	STMEA sp!, {r0-r11}				;store current registers value
	B Bi_SearchMain						;call Binary serach function


SearchSuccess	
	SUB r5, r4, r10							;if find key data, compute index & store
	MOV r5, r5, LSR #2			
	STRH r5, [r11], #2			
	ADD r12, r12, #1						;r12 is number of success search data , r12++
				
SearchFail				
	ADD r9, r9, #1							;count++
	CMP r9, #101							;if count==101, exit function
	LDMEANE	sp!, {r0, r3}
	BEQ Exit
	
Bi_SearchMain
	LDR	r2, [r1], #4						;key of search
	STMEA sp!, {r0, r3}					;store r0~r3 in stack
		
Bi_Search	
	CMP r3, r0								;if last<first , go to SearchFail
	BLT SearchFail						
		
	ADD r4, r0, r3							;midium addr=(first+last)/2
	MOV r4, r4, ASR #3	
	MOV r4, r4, LSL #2		
		
	LDR r5, [r4]								;r5 is arr[mid]
	CMP r2, r5								;compare search data(key) & arr[mid]
		
	SUBLT r3, r4, #4						;if key<arr[mid], last addr=midium addr-4
	ADDGT r0, r4, #4						;else if key>arr[mid], first addr=midium addr+4
	BNE Bi_Search						;repeat loop
	BEQ SearchSuccess				;else, go to SearchSuccess
		

	
	;start Sort
QuickSort
	STMEA sp!, {r0-r12, lr}
	SUB	r2, r1, #4							;right = r2
	MOV r1, r0								;left=r1
	STMEA	sp!, {r1, r2}					;store r1, r2 in stack
	LDR r12,=0x3038						;r12 is address for end of function
	ADD r11, r0, #4							;r11=left+1
		
QLoop	
	LDMEA sp!, {r1, r2}					;right & left set from stack data
	MOV lr, pc								;lr=pc
    B Partition								;call Partition function
		
	CMP r4, r1								;if pivot-1>left
	SUBGT	r4, r3, #4						;right=pivot-1
	STMEAGT sp!, {r1, r4}				;store left right
		
	ADD r0, r3, #4							;pivot+1
	CMP r0, r2								;if pivot+1<right, left=pivot+1
	STMEALT sp!, {r0, r2}				;store left & right in stack
	BLT QLoop 								;repeat loop
													
													;else,
	CMP sp, r12								;if stack is empty, exit
	LDMEAEQ sp!, {r0-r12, pc}		
	B QLoop									;else repeat loop
		
	
Partition
	STMEA	sp!, {r1, r2, lr}				;store r1, r2, lr in stack
	LDR r4, [r2]								;r4=pivot
	SUB r3, r1, #4							;r3=i
	MOV r5, r1								;r5=j
	
PLoop
	LDR	r6, [r5]							;r6=arr[j]
	CMP r6, r4								;arr[j]<=pivot
	ADDLE	r3, r3, #4						;i++
	SWPLE r7,  r6, [r3]					;swap -> arr[i] , arr[j]
	STRLE	r7, [r5]
	ADD r5, r5, #4
	CMP r5, r2								;j>=max, exit
	BGE PartitionExit
	
	LDR	r6, [r5]							;r6=arr[j]
	CMP r6, r4								;arr[j]<=pivot
	ADDLE	r3, r3, #4						;i++
	SWPLE r7,  r6, [r3]					;swap -> arr[i] , arr[j]
	STRLE	r7, [r5]
	ADD r5, r5, #4
	CMP r5, r2								;j>=max, exit
	BGE PartitionExit
	
	LDR	r6, [r5]							;r6=arr[j]
	CMP r6, r4								;arr[j]<=pivot
	ADDLE	r3, r3, #4						;i++
	SWPLE r7,  r6, [r3]					;swap -> arr[i] , arr[j]
	STRLE	r7, [r5]
	ADD r5, r5, #4
	CMP r5, r2								;j>=max, exit
	BGE PartitionExit
	
	LDR	r6, [r5]							;r6=arr[j]
	CMP r6, r4								;arr[j]<=pivot
	ADDLE	r3, r3, #4						;i++
	SWPLE r7,  r6, [r3]					;swap -> arr[i] , arr[j]
	STRLE	r7, [r5]
	ADD r5, r5, #4
	CMP r5, r2								;j>=max, exit
	BGE PartitionExit
	
	LDR	r6, [r5]							;r6=arr[j]
	CMP r6, r4								;arr[j]<=pivot
	ADDLE	r3, r3, #4						;i++
	SWPLE r7,  r6, [r3]					;swap -> arr[i] , arr[j]
	STRLE	r7, [r5]
	ADD r5, r5, #4
	CMP r5, r2								;j>=max, exit
	BLT PLoop

PartitionExit
	ADD r3, r3, #4							;swap -> arr[i+1] , pivot
	SWP r7,  r4, [r3]
	STR	r7, [r2]
	SUB r4, r3, #4
	LDMEA sp!, {r1, r2, pc}			;return i+1


Exit
	LDMEA	sp!, {r0, r3}					;load r0~r3 
	LDMEA sp!, {r0-r11}				;load original value of registers to stack
	MOV pc, #0
	
ST & &00003000
Result & &40000000


